'use strict'
const _package = require('./package.json.js')
const noNegatedConjunction = require('./rules/no-negated-conjunction.js')
const noNegatedDisjunction = require('./rules/no-negated-disjunction.js')
let pluginName = 'de-morgan'
let rules = {
  'no-negated-conjunction': noNegatedConjunction,
  'no-negated-disjunction': noNegatedDisjunction,
}
let getRules = () =>
  Object.fromEntries(
    Object.keys(rules).map(ruleName => [`${pluginName}/${ruleName}`, 'error']),
  )
let createConfig = () => ({
  plugins: {
    [pluginName]: {
      rules,
    },
  },
  rules: getRules(),
})
let createLegacyConfig = () => ({
  plugins: [pluginName],
  rules: getRules(),
})
const index = {
  configs: {
    'recommended-legacy': createLegacyConfig(),
    recommended: createConfig(),
  },
  meta: {
    version: _package.version,
    name: _package.name,
  },
  rules,
}
module.exports = index
